public class bhabho {
    public static void bhab(String str,int idx)
    {
        if (idx==0)
        {
            System.out.println(str.charAt(idx));
            return;
        }
        System.out.println(str.charAt(idx));
        bhab(str, idx-1);
    }
    public static void main(String[] args) {
        String str="abcd";
        bhab(str,str.length()-1                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            );
    }
}
